import React from "react";
import { connect, useSelector } from "react-redux";
import { remove } from "../store/CartReducer";

import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import PopupState, { bindTrigger, bindMenu } from "material-ui-popup-state";
import BackspaceIcon from "@material-ui/icons/Backspace";

const stateMapProps = (state) => ({
  UserCart: state.CartReducer,
});

const mapDispatchToProps = { remove };

const Cart = (props) => {
  return (
    <PopupState variant="popover" popupId="demo-popup-menu">
      {(popupState) => (
        <React.Fragment>
          <Button className="myCart" {...bindTrigger(popupState)}>
            CART ({props.UserCart.cart.length})
          </Button>
          <Menu {...bindMenu(popupState)}>
            {props.UserCart.cart.map((item, idx) => {
              {
                return (
                  <>
                    <MenuItem>
                      {item.name} &nbsp;{" "}
                      <BackspaceIcon onClick={() => props.remove(item)} />
                    </MenuItem>
                  </>
                );
              }
            })}
          </Menu>
        </React.Fragment>
      )}
    </PopupState>
  );
};


export default connect(stateMapProps, mapDispatchToProps)(Cart);
