let initialState = {
  categories: [
    { categoryNormalizedName: "Flowers", displayName: "Flowers", description: "" },
    {
      categoryNormalizedName: "Bags",
      displayName: "Bags",
      description: "",
    },
  ],
  activeCategory: "",
};

export default (state = initialState, action) => {

  let { type, payload } = action;

  switch (type) {
    case "ACTIVE":
      return
      return state;
  }
};

export const activeCatFun = (Categories) => {
  return {
    type: "ACTIVE",
    payload: Categories,
  };
};
