import React from "react";
import { connect } from "react-redux";
import { activeCatFun } from "../store/CategoryStore";

import Paper from "@material-ui/core/Paper";
// import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";

const Categories = (props) => {
  return (

    <>
      <h1>Browse our Categories</h1>

      <Paper square>
        {props.categories.map((Categories, idx) => {
          return (
            <>
              <Tab
                color="secondary"
                label={Categories.displayName}
                key={idx}
                onClick={() => props.activeCatFun(Categories)}
              ></Tab>
            </>
          );
        })}
      </Paper>
    </>



  );
};

const mapStateToProps = (state) => ({
  categories: state.CatReducer.categories,
});

const mapDispatchToProps = { activeCatFun };

export default connect(mapStateToProps, mapDispatchToProps)(Categories);
