import React from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import { Card, CardContent, CardMedia, CardActions, Typography, Button } from "@material-ui/core";







const stateMapProps = (state) => ({

  Prod: state.ProductReducer.listOfProducts,
  categories: state.CatReducer.categories,
});


const styling = makeStyles({
  root: {
    maxWidth: 350,
    display: "inline-block",
    margin: 50
  },
  media: {
    height: 240,
  },
});

function Products(props) {
  const classes = styling();



  return (
    <>

      {/* {props.Prod}  */}
      {/* <h1>Flowers</h1> */}

      {props.Prod.map((it, i) => {
        return (
          <>

div
            <Card key={i} className={classes.root} id="cards">
              <CardMedia
                className={classes.media}
                image={it.image} />
              <CardContent>

                <Typography gutterBottom variant="h5" component="h2">
                  {it.name}
                </Typography>

                <Typography variant="body2" color="textSecondary" component="p">
                  {it.price}$
                </Typography>

              </CardContent>

              <CardActions>

                <Button size="small" color="primary">
                  Add to Cart
                </Button>

                <Button size="small" color="primary">
                  View Details
                </Button>

              </CardActions>
            </Card>
          </>
        );
      })}
    </>
  );
}

export default connect(stateMapProps)(Products);
