let initialProductState = {
  products: [
    {
      category: "Flowers",
      name: "Tulip",
      description: "",
      price: 45,
      inventoryCount: 5,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/819zhtPix4L._SY450_.jpg",
    },
    {
      category: "Bags",
      name: "",
      description: "",
      price: 400,
      inventoryCount: 2,
      image:
        "https://www.google.com/url?sa=i&url=https%3A%2F%2Fpaytmmall.com%2Fwomen-handbags-glpid-7468&psig=AOvVaw1F8P41l-a4fi9OYwZMmHH9&ust=1625547356044000&source=images&cd=vfe&ved=0CAoQjRxqFwoTCNitq4OSy_ECFQAAAAAdAAAAABAD",
    },
    {
      category: "Bags",
      name: "Iphone 11",
      description: "",
      price: 500,
      inventoryCount: 2,
      image:
        "https://cdn.pocket-lint.com/r/s/1200x/assets/images/149324-phones-review-review-iphone-11-pro-max-review-product-shots-image1-keant0hfcg.jpg",
    },
  ],

  productsList: [],
};

const ProductReducer = (state = initialProductState, action) => {
  let { type, payload } = action;
  switch (type) {
    case "ACTIVE":
      state.productsList = [];
      state.products.forEach((product) => {
        if (product.category === payload.normalizedName) {
          state.productsList.push(product);
        }
      });
      return { products: state.products, productsList: state.productsList };

    default:
      return state;
  }
};

export default ProductReducer;
