let productState = {
  products: [
    {
      category: "Flowers",
      name: "Tulip",
      description: "",
      price: 45,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/819zhtPix4L._SY450_.jpg",
    },
    {
      category: "Bags",
      name: "CHANEL",
      description: "",
      price: 900,
      image:
        "https://katiesbliss.com/wp-content/uploads/2019/03/CHANEL-Classic-Medium-Flap-Bag-Black-1.jpg",
    },
    {
      category: "Bags",
      name: "GUCCI",
      description: "",
      price: 890,
      image:
        "https://i.pinimg.com/736x/c5/2f/f7/c52ff779d1d27112253dd26d4de74c45.jpg",
    },
  ],

  productsList: [],
};

const ProductReducer = (state = productState, action) => {
  let { type, payload } = action;
  switch (type) {
    case "ACTIVE":
      state.productsList = [];
      state.products.forEach((product) => {
        if (product.category === payload.categoryNormalizedName) {
          state.productsList.push(product);
        }
      });
      return { products: state.products, productsList: state.productsList };

    default:
      return state;
  }
};

export default ProductReducer;
